pub enum Error {
    Io(io::Error),
    UnknownOpCode,
    ReservedOpCode,
    FragmentedControl,
    InvalidControlFrameLen,
    InvalidReservedBit(u8),
    PayloadTooLarge {
            actual: u64,
            maximum: u64,
        },
}

pub enum Error {
    Io(io::Error),
    Codec(base::Error),
    Extension(crate::BoxedError),
    UnexpectedOpCode(OpCode),
    Utf8(str::Utf8Error),
    MessageTooLarge {
            current: usize,
            maximum: usize,
        },
    Closed,
}

pub enum ServerResponse {
    Accepted {
            protocol: Option<String>,
        },
    Redirect {
            status_code: u16,
            location: String,
        },
    Rejected {
            status_code: u16,
        },
}

pub enum Response<'a> {
    Accept {
            key: WebSocketKey,
            protocol: Option<&'a str>,
        },
    Reject {
            status_code: u16,
        },
}

pub enum Parsing<T, N = ()> {
    Done {
            value: T,
            offset: usize,
        },
    NeedMore(N),
}

pub enum Storage<'a> {
    Shared(&'a [u8]),
    Unique(&'a mut [u8]),
    Owned(Vec<u8>),
}
