pub enum BlockhashQuery {
    None(Hash),
    FeeCalculator(Source, Hash),
    All(Source),
}

pub enum PubsubClientError {
    UrlParseError(url::ParseError),
    ConnectionError(tokio_tungstenite::tungstenite::Error),
    WsError(tokio_tungstenite::tungstenite::Error),
    ConnectionClosed(String),
    JsonParseError(serde_json::error::Error),
    SubscribeFailed {
            reason: String,
            message: String,
        },
}

pub enum Error {
    InvalidAccountOwner,
    InvalidAccountData,
    UnexpectedDataSize,
    InvalidHash {
            provided: Hash,
            expected: Hash,
        },
    InvalidAuthority {
            provided: Pubkey,
            expected: Pubkey,
        },
    InvalidStateForOperation,
    Client(String),
}

pub enum PubsubClientError {
    UrlParseError(ParseError),
    ConnectionError(tungstenite::Error),
    JsonParseError(serde_json::error::Error),
    UnexpectedMessageError(String),
}

pub enum RpcTransactionLogsFilter {
    All,
    AllWithVotes,
    Mentions(Vec<String>),
}

pub enum RpcCustomError {
    BlockCleanedUp {
            slot: Slot,
            first_available_block: Slot,
        },
    SendTransactionPreflightFailure {
            message: String,
            result: RpcSimulateTransactionResult,
        },
    TransactionSignatureVerificationFailure,
    BlockNotAvailable {
            slot: Slot,
        },
    NodeUnhealthy {
            num_slots_behind: Option<Slot>,
        },
    TransactionPrecompileVerificationFailure(solana_sdk::transaction::TransactionError),
    SlotSkipped {
            slot: Slot,
        },
    NoSnapshot,
    LongTermStorageSlotSkipped {
            slot: Slot,
        },
    KeyExcludedFromSecondaryIndex {
            index_key: String,
        },
    TransactionHistoryNotAvailable,
    ScanError {
            message: String,
        },
    TransactionSignatureLenMismatch,
    BlockStatusNotAvailableYet {
            slot: Slot,
        },
    UnsupportedTransactionVersion(u8),
    MinContextSlotNotReached {
            context_slot: Slot,
        },
}

pub enum MemcmpEncodedBytes {
    Binary(String),
    Base58(String),
    Base64(String),
    Bytes(Vec<u8>),
}

pub enum RpcResponseErrorData {
    Empty,
    SendTransactionPreflightFailure(RpcSimulateTransactionResult),
    NodeUnhealthy {
            num_slots_behind: Option<Slot>,
        },
}

pub enum RpcError {
    RpcRequestError(String),
    RpcResponseError {
            code: i64,
            message: String,
            data: RpcResponseErrorData,
        },
    ParseError(String),
    ForUser(String),
}

pub enum SlotUpdate {
    FirstShredReceived {
            slot: Slot,
            timestamp: u64,
        },
    Completed {
            slot: Slot,
            timestamp: u64,
        },
    CreatedBank {
            slot: Slot,
            parent: Slot,
            timestamp: u64,
        },
    Frozen {
            slot: Slot,
            timestamp: u64,
            stats: SlotTransactionStats,
        },
    Dead {
            slot: Slot,
            timestamp: u64,
            err: String,
        },
    OptimisticConfirmation {
            slot: Slot,
            timestamp: u64,
        },
    Root {
            slot: Slot,
            timestamp: u64,
        },
}
