pub enum CommitTransactionDetails {
    Committed {
            compute_units: u64,
        },
    NotCommitted,
}

pub enum BufferedPacketsDecision {
    Consume(u128),
    Forward,
    ForwardAndHold,
    Hold,
}

pub enum BroadcastStageType {
    Standard,
    FailEntryVerification,
    BroadcastFakeShreds,
    BroadcastDuplicates(BroadcastDuplicatesConfig),
}

pub enum SwitchForkDecision {
    SwitchProof(Hash),
    SameFork,
    FailedSwitchThreshold(u64, u64),
    FailedSwitchDuplicateRollback(Slot),
}

pub enum CostUpdate {
    FrozenBank {
            bank: Arc<Bank>,
        },
    ExecuteTiming {
            execute_timings: Box<ExecuteTimings>,
        },
}

pub enum QosMetrics {
    BlockBatchUpdate {
            bank: Arc<Bank>,
        },
}

pub enum HeaviestForkFailures {
    LockedOut(u64),
    FailedThreshold(u64),
    FailedSwitchThreshold(u64),
    NoPropagatedConfirmation(u64),
}

pub enum ShredRepairType {
    Orphan(Slot),
    HighestShred(Slot, u64),
    Shred(Slot, u64),
}

pub enum VoteOp {
    PushVote {
            tx: Transaction,
            tower_slots: Vec<Slot>,
            saved_tower: SavedTowerVersions,
        },
    RefreshVote {
            tx: Transaction,
            last_voted_slot: Slot,
        },
}
