pub enum InsertDataShredError {
    Exists,
    InvalidShred,
    BlockstoreError(BlockstoreError),
}

pub enum AccessType {
    PrimaryOnly,
    PrimaryOnlyForMaintenance,
    TryPrimaryThenSecondary,
}

pub enum BlockstoreRecoveryMode {
    TolerateCorruptedTailRecords,
    AbsoluteConsistency,
    PointInTime,
    SkipAnyCorruptedRecord,
}

pub enum ShredError {
    InvalidShredType,
    InvalidFecRate(f32),
    SlotTooLow {
            slot: Slot,
            parent_slot: Slot,
        },
    Serialize(Box<bincode::ErrorKind>),
    InvalidParentOffset {
            slot: Slot,
            parent_offset: u16,
        },
    InvalidPayload,
}
